import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Loader from 'react-loaders'
import AnimatedLetters from '../AnimatedLetters'
import './index.scss';
import Logo from './Logo'
import Valorant from '../../assets/images/ValorantGamerGif.gif';

const Home = () => {


    const scrollElements = document.querySelectorAll(".js-scroll");

const elementInView = (el, dividend = 1) => {
  const elementTop = el.getBoundingClientRect().top;

  return (
    elementTop <=
    (window.innerHeight || document.documentElement.clientHeight) / dividend
  );
};

const elementOutofView = (el) => {
  const elementTop = el.getBoundingClientRect().top;

  return (
    elementTop > (window.innerHeight || document.documentElement.clientHeight)
  );
};

const displayScrollElement = (element) => {
  element.classList.add("scrolled");
};

const hideScrollElement = (element) => {
  element.classList.remove("scrolled");
};

const handleScrollAnimation = () => {
  scrollElements.forEach((el) => {
    if (elementInView(el, 1.25)) {
      displayScrollElement(el);
    } else if (elementOutofView(el)) {
      hideScrollElement(el)
    }
  })
}

window.addEventListener("scroll", () => { 
  handleScrollAnimation();
});

    const [letterClass, setLetterClass] = useState('text-animate')

    const nameArray = ['J','a', 's', 'o', 'n', ' ', 'M', 'a', 'i']
    const jobArray = ['a', ' ', 'S', 'o', 'f','t','w','a','r','e', ' ','E','n','g','i','n','e','e','r']

    useEffect(() => {
        setTimeout(() => {
          setLetterClass('text-animate-hover')
        }, 4000)
      }, [])

    return (
        <>
            <div className="container home-page">
                <div className="text-zone">
                    <h1>
                    <span className={letterClass}>H</span>
                    <span className={`${letterClass} _12`}>e</span>
                    <span className={`${letterClass} _13`}>l</span>
                    <span className={`${letterClass} _14`}>l</span>
                    <span className={`${letterClass} _15`}>o</span>
                    <span className={`${letterClass} _16`}>,</span>
                    <br />
                    <span className={`${letterClass} _17`}>I</span>
                    <span className={`${letterClass} _18`}>'</span>
                    <span className={`${letterClass} _19`}>m</span>
                    &nbsp;



                    <AnimatedLetters letterClass = {letterClass} 
                    strArray = {nameArray}
                    idx = {15} />
                    <br />
                    <AnimatedLetters letterClass = {letterClass} 
                    strArray = {jobArray}
                    idx = {17} />
                    </h1>
                    <h2>Web/Mobile App Developer | Swift Expert | Content Creator</h2>
                    <a href="https://www.linkedin.com/in/jason-mai-460583196/details/featured/1635494628427/single-media-viewer/">
                    <div className = 'flat-button1'>RESUME</div>
                    </a>
                    <Link to = "/contact" className = 'flat-button2'>CONTACT ME</Link>
                </div>
                <Logo />

                <div className = "lookDownText"><b><p>Scroll Down</p></b></div>
                <div className = "ValorantGamer">
                    <img src = {Valorant} alt = "This will display an animated gif for Valorant"/>
                    <div className = "ValDescription">
                        <h1> I am a Gamer</h1>
                    </div>
                </div>
            </div>




            <header class="container">
  <h1>How to Animate on Scroll With Vanilla JavaScript</h1>
  <h2>Scroll to see the effects
    <p class="animate-arrow">&darr;
    </p>
  </h2>
</header>
<section class="scroll-container">
  <div class="scroll-element js-scroll fade-in">

  </div>
  <div class="scroll-caption">
    This animation fades in.
  </div>
</section>
<section class="scroll-container">
  <div class="scroll-element js-scroll fade-in-bottom">

  </div>
  <div class="scroll-caption">
    This animation slides in to the top.
  </div>
</section>
<section class="scroll-container">
  <div class="scroll-element js-scroll slide-left">

  </div>
  <div class="scroll-caption">
    This animation slides in from the left.
  </div>
</section>
<section class="scroll-container">
  <div class="scroll-element js-scroll slide-right">

  </div>
  <div class="scroll-caption">
    This animation slides in from the right.
  </div>
</section>




            <Loader type="pacman" />
        </>
    );
}




export default Home;
