import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Loader from 'react-loaders'
import AnimatedLetters from '../AnimatedLetters'
import './index.scss';
import Logo from './Logo'
import Valorant from '../../assets/images/ValorantGamerGif.gif';

const Home = () => {
    const [letterClass, setLetterClass] = useState('text-animate')

    const nameArray = ['J','a', 's', 'o', 'n', ' ', 'M', 'a', 'i']
    const jobArray = ['a', ' ', 'S', 'o', 'f','t','w','a','r','e', ' ','E','n','g','i','n','e','e','r']

    useEffect(() => {
        setTimeout(() => {
          setLetterClass('text-animate-hover')
        }, 4000)
      }, [])

    return (
        <>
            <div className="container home-page">
                <div className="text-zone">
                    <h1>
                    <span className={letterClass}>H</span>
                    <span className={`${letterClass} _12`}>e</span>
                    <span className={`${letterClass} _13`}>l</span>
                    <span className={`${letterClass} _14`}>l</span>
                    <span className={`${letterClass} _15`}>o</span>
                    <span className={`${letterClass} _16`}>,</span>
                    <br />
                    <span className={`${letterClass} _17`}>I</span>
                    <span className={`${letterClass} _18`}>'</span>
                    <span className={`${letterClass} _19`}>m</span>
                    &nbsp;



                    <AnimatedLetters letterClass = {letterClass} 
                    strArray = {nameArray}
                    idx = {15} />
                    <br />
                    <AnimatedLetters letterClass = {letterClass} 
                    strArray = {jobArray}
                    idx = {17} />
                    </h1>
                    <h2>Web/Mobile App Developer | Swift Expert | Content Creator</h2>
                    <a href="https://www.linkedin.com/in/jason-mai-460583196/details/featured/1635494628427/single-media-viewer/">
                    <div className = 'flat-button1'>RESUME</div>
                    </a>
                    <Link to = "/contact" className = 'flat-button2'>CONTACT ME</Link>
                </div>
                <Logo />

                <div className = "lookDownText"><b><p>Scroll Down</p></b></div>
                <div className = "ValorantGamer">
                    <img src = {Valorant} alt = "This will display an animated gif for Valorant"/>
                    <div className = "ValDescription">
                        <h1> I am a Gamer</h1>
                    </div>
                </div>
            </div>


            <h1 class="intro-text">Scroll Down To Reveal Stuff!</h1>

    <div class="scroll-reveal box left">
        I came from left
    </div>
    <div class="scroll-reveal box right">
        I came from right
    </div>
    <div class="scroll-reveal box top">
        I came from top rotating
    </div>

    <h1 class="loader">hello</h1>

    <div class="scroll-reveal box bottom">
        I came from bottom scaled
    </div>


            <Loader type="pacman" />
        </>
    );
}



export default Home;



function scrollTrigger(selector, options = {}) {
    let els = document.querySelectorAll(selector)
    els = Array.from(els)
    els.forEach(el => {
      addObserver(el, options)
    })
  }
  function addObserver(el, options) {
    // Check if `IntersectionObserver` is supported
    if(!('IntersectionObserver' in window)) {
      // Simple fallback
      // The animation/callback will be called immediately so
      // the scroll animation doesn't happen on unsupported browsers
      if(options.cb){
        options.cb(el)
      } else{
        entry.target.classList.add('active')
      }
      // We don't need to execute the rest of the code
      return
    }
    let observer = new IntersectionObserver((entries, observer) => {
      entries.forEach(entry => {
        if(entry.isIntersecting) {
          if(options.cb) {
            options.cb(el)
          } else{
            entry.target.classList.add('active')
          }
          observer.unobserve(entry.target)
        }
      })
    }, options)
    observer.observe(el)
  }
  // Example usages:
  scrollTrigger('.intro-text')
  scrollTrigger('.scroll-reveal', {
    rootMargin: '-200px',
  })
  scrollTrigger('.loader', {
    rootMargin: '-200px',
    cb: function(el){
      el.innerText = 'Loading...'
      setTimeout(() => {
        el.innerText = 'Task Complete!'
      }, 1000)
    }
  })
